function Get-OctopusVariable {
    param(
		[Parameter(Position=1,Mandatory=$true)]$Name,
		[switch]$AllowNull
	)
  
    $variableValue = $OctopusParameters.GetEnumerator() | ? Key -like "Octopus.Action[[]*[]].Output.${Name}" | % Value
    if ($variableValue -or $AllowNull) { return $variableValue }
    else { throw "Unable to find Octopus Variable $Name" }
}

function Export-DebugInfo {
    $debugFolder = Join-Path 'D:\' ($pwd | Split-Path -Leaf)
    Copy-Item $PWD $debugFolder -Recurse -Force
    ls variable: | Out-File (Join-Path $debugFolder 'variable.txt')
    ls env: | Out-File (Join-Path $debugFolder 'env.txt')
    Write-Warning "Debug Folder: $debugFolder"
}
